    double num1, num2 ,result ;
    char ope = ' ';
    
   
    public void setAll(String name)                                //to write each form of each button in text_field
    {
        String text = jTextField1.getText() + name;
        jTextField1.setText(text);
    }
   
    public void set_operation(char operation )
    {
        num1 = Double.parseDouble(jTextField1.getText());
        copy_text(operation);
        ope = operation;        
    }
    
    public void copy_text(char sign)         //to copy equation from the first txt_field to second txt_field and delete text from txt_field1
    {
        String equ = jTextField1.getText();
        jTextField2.setText(jTextField2.getText() + equ + sign );
        jTextField1.setText("");
    }
    
    public void operation_test(char operation2)
    {
        if(ope == ' ')
        {
            set_operation(operation2);
        }
    }
    
    public static double factorial(double n)
    { 
        if(n >= 0)
        {
            if (n == 0)
                return 1;
            else
            {
                return n*factorial(n-1);
            }
        }
        else
        {
             if (n == 0)
                return -1;
            else
            {
                return n*factorial(n+1);
            }
        }
    }
    
